E_D("DROP TABLE IF EXISTS `travel_products`;");
E_C("CREATE TABLE `travel_products` (
  `p_id` int(10) NOT NULL auto_increment,
  `p_title` varchar(100) default NULL,
  `p_title2` varchar(100) NOT NULL,
  `p_no` varchar(32) NOT NULL,
  `p_aid` int(11) NOT NULL COMMENT '地区id',
  `p_detail` text COMMENT '摘要',
  `p_arrival_one` varchar(100) NOT NULL,
  `p_arrival_two` varchar(200) default NULL,
  `p_arrival_three` varchar(200) NOT NULL,
  `p_departure_province` varchar(100) NOT NULL,
  `p_departure_city` varchar(200) NOT NULL,
  `p_enddate` int(11) NOT NULL,
  `p_departure_time` varchar(50) NOT NULL,
  `p_tel` varchar(50) NOT NULL,
  `p_travel_days` int(5) NOT NULL COMMENT '旅游天数',
  `p_market_price` int(11) NOT NULL,
  `p_price` int(11) NOT NULL,
  `p_hot` int(11) NOT NULL COMMENT '关注度',
  `p_signup` varchar(100) NOT NULL COMMENT '提前报名',
  `p_stay` varchar(32) NOT NULL,
  `p_transport` varchar(32) NOT NULL COMMENT '往返交通工具',
  `p_characteristic` text NOT NULL,
  `p_visa` text NOT NULL COMMENT '签证须知',
  `p_fees` text NOT NULL COMMENT '费用包含',
  `p_tips` text NOT NULL COMMENT '温馨提示',
  `p_addtime` int(10) NOT NULL,
  `catid` int(11) default NULL,
  `p_type` tinyint(4) default NULL,
  `is_show` tinyint(1) default '0',
  `p_keywords` varchar(100) default NULL,
  `p_extension` varchar(100) NOT NULL,
  `p_smallpic` varchar(100) NOT NULL,
  `p_pics` text,
  `p_page` varchar(50) NOT NULL,
  `p_tpl` varchar(50) default NULL,
  `p_updatetime` int(11) NOT NULL,
  `p_hits` int(11) NOT NULL default '0',
  `recommend` tinyint(1) NOT NULL,
  `recommend_home` tinyint(1) NOT NULL,
  `sp_recommend_home` int(1) NOT NULL,
  `promotions` tinyint(1) NOT NULL,
  `ishot` int(11) NOT NULL,
  `orderid` int(11) default NULL,
  PRIMARY KEY  (`p_id`),
  KEY `p_keywords` (`p_keywords`),
  KEY `p_aid` (`p_aid`),
  KEY `p_price` (`p_price`),
  KEY `p_travel_days` (`p_travel_days`),
  KEY `catid` (`catid`)
) ENGINE=MyISAM AUTO_INCREMENT=556 DEFAULT CHARSET=utf8");
E_D("replace into `travel_products` values('189','海口非2蓝梦之旅海口非2蓝梦之旅','三亚2日游三亚2日游','10897','2','5A南山／热带天堂公园／分界洲／天涯海角／玉带滩／热带植物园／大东海／椰林乡村生态旅游区／亚龙湾旅游度假区\r\n  独特安排:“非诚勿扰2” 情美大片拍摄地【亚龙湾热带天堂森林公园风景区】\r\n','5','13','19','1','2','0','天天发团','4006-365-001 010-58301186','5','5800','5380','208','提前三天报名','电联','飞机','&lt;p&gt;\r\n	◎高档酒店：全程挂四或准五标准指定酒店&lt;br /&gt;\r\n	◎精华景点：5A南山／热带天堂公园／分界洲／天涯海角／玉带滩／热带植物园／大东海／椰林乡村生态旅游区／亚龙湾旅游度假区&lt;br /&gt;\r\n	独特安排:&amp;ldquo;非诚勿扰2&amp;rdquo; 情美大片拍摄地【亚龙湾热带天堂森林公园风景区】&lt;br /&gt;\r\n	◎导游服务：专职优秀导游，每位导游均经严格筛选，经验丰富、知识面广、讲解幽默风趣灵活、服务意识强，保证全程服务无忧。&lt;br /&gt;\r\n	◎购物安排：海南最具特色的水晶店，国内首家大型免税购物商场&amp;mdash;&amp;mdash;免税店&lt;br /&gt;\r\n	◎郑重承诺：绝不推任何自费景点（特色项目除外），绝不更换景点&lt;/p&gt;\r\n','','&lt;p&gt;\r\n	1、旅游交通：海南一地全程空调旅游车，由于实行的是全省旅游用车滚动发班，确保每一游客一个正坐.&lt;br /&gt;\r\n	2、住宿：全程挂四或准五标准指定酒店。（出现单男单女，客人需补房差或安排住三人间）&lt;br /&gt;\r\n	海口住：汇银酒店／丽华酒店／奥嘉阳光／泰华酒店／豪威麒麟／新标榜／金银湾／腾鹏酒店／蓝海阳光/或同级&lt;br /&gt;\r\n	兴隆住：鑫桥酒店／金叶桃园酒店／温泉宾馆／金日酒店／正昊寿仙／钓鱼台／明珠温泉／乐金宵或同级&lt;br /&gt;\r\n	三亚住：凯丰酒店／天泽海韵／世纪山水／金都／美丽春天／金豪/汇融海景／二月海／豪威海景／科宝庄园或同级&lt;br /&gt;\r\n	注意：如遇旺季酒店资源紧张或政府临时征用等特殊情况，我社有权调整同等级标准酒店&lt;br /&gt;\r\n	3、景点门票：行程内已含景点第一道门票。另行付费景点门票费用由客人承担。&lt;/p&gt;\r\n','&lt;p&gt;\r\n	特别说明：在不影响原所包含景点的游览时间和行程安排下，遵循少数服从多数的原则，不参加另行付费项目的游客需等待。如有异议，以我公司与组团社产品行程确认件为准。&amp;ldquo;赠送项目如果突遇特殊原因取消不退任何费用，并且不更换景点；如因游客自身原因擅自离团或者因为自然灾害等所有不可抗拒因素所造成的额外费用必须由客人自己承担；游客如果自愿离团或放弃旅游项目【包括放弃餐饮、景点、住宿或持有所有优惠证件】将不退任何费用。&lt;/p&gt;\r\n','1348105996','2','0','1','|亚洲|韩国|济州岛|','1111','','','189.html','products.html','0','0','1','1','1','0','1','1');");
E_D("replace into `travel_products` values('218','<华东五市-瘦西湖-乌镇西栅-西溪湿地品质游>','囊括华东著名景点：瘦西湖、中山陵、个园、灵山大佛、虎丘、西溪湿地、船游西湖、水乡乌镇','XZMHDFC','0','囊括华东著名景点：瘦西湖、中山陵、个园、灵山大佛、虎丘、西溪湿地、船游西湖、水乡乌镇；','6|34|33','58|76|83|84|85|77|78|79','','11','56','2013','天天','010-5830 1186；4006-365-001','7','1780','1680','666','10天','三星酒店双人标准间','火车/高铁/飞机','&lt;p&gt;\r\n	1、景点安排：囊括华东著名景点：瘦西湖、中山陵、个园、灵山大佛、虎丘、西溪湿地、船游西湖、水乡乌镇；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、&amp;nbsp;住宿安排：3晚挂牌三星酒店，特别赠送1晚挂牌四星级酒店住宿；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、、车辆安排：专属空调旅游车，保证1人1个座位；&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、用餐安排：自助早餐，正餐提升20元/人/餐；&lt;/p&gt;\r\n&lt;p&gt;\r\n	5、&amp;nbsp;导游安排：专属优秀中文导游旅行及讲解服务；&lt;/p&gt;\r\n&lt;p&gt;\r\n	6、&amp;nbsp;购物安排：全程只进2个购物店，赏千亩茶园风光，赠送集体合照；&lt;/p&gt;\r\n&lt;p&gt;\r\n	7、郑重承诺：行程中无强制消费，自由选择行程内推荐的自费景点、娱乐项目或其它经游客允许的消费活动！&lt;/p&gt;\r\n','','&lt;p&gt;\r\n	一、费用包含&lt;/p&gt;\r\n&lt;p&gt;\r\n	住宿：三晚挂牌3星或准4星酒店双人标准间+一晚挂牌4星酒店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 如出现单男或单女请补交房差或拼住，不提供自然单间。共计400元；&lt;/p&gt;\r\n&lt;p&gt;\r\n	用餐：4早6正，8菜1汤，自助早餐，正餐20元/人，10人1桌，人数不足菜量相应减少；&lt;/p&gt;\r\n&lt;p&gt;\r\n	交通：去程空调火车卧铺或高铁二等、飞机；返程空调火车卧铺或高铁二等座、飞机。&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 当地空调旅游车1人1座，全程1台车，26座以下均没有行礼箱；&lt;/p&gt;\r\n&lt;p&gt;\r\n	门票：景点首道大门票，如有小门票客人自理；&lt;/p&gt;\r\n&lt;p&gt;\r\n	导游：当地1个导游；&lt;/p&gt;\r\n&lt;p&gt;\r\n	二、费用不含&lt;/p&gt;\r\n&lt;p&gt;\r\n	保险：建议游客出行前自行购买旅游人身意外伤害保险及航空保险；&lt;/p&gt;\r\n&lt;p&gt;\r\n	车票差价：旺季期间火车票非常紧张，往返火车票均按行程中标注的价格核算，如出现火车软卧、动车等特殊情况，请游客按照票面补齐火车票差价，旺季不保证火车进出地点。&lt;/p&gt;\r\n&lt;p&gt;\r\n	儿童：火车团儿童只含当地车位及半价餐，飞机团12周岁以下儿童只含飞机票、当地车位及半价餐；&lt;/p&gt;\r\n&lt;p&gt;\r\n	儿童：占床4晚加400元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	三、自费项目&lt;/p&gt;\r\n&lt;p&gt;\r\n	以下项目为公司推荐的旅游项目，游客自愿参加需要另外付费，自理景点因含有导游司机加班费故一切优惠证件不予使用。如游客持证自行游览自费景点，另加收车费100元/人/点。不参加者须自行前往酒店费用自理或在导游指定地点自由活动等侯参加游览的游客到齐后统一送回酒店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、【扬州千秋粉黛】100元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、【无锡灵山大佛】210元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、【吴越千古情】260元/人&amp;mdash;300元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、【上海夜景影视乐园套票】320元/人&lt;/p&gt;\r\n&lt;p&gt;\r\n	四、购物安排&lt;/p&gt;\r\n&lt;p&gt;\r\n	购物：进2个店：【丝绸】杭州天蚕丝绸（1h）、【珍珠】苏州太湖珍珠（1h）&lt;/p&gt;\r\n','&lt;p&gt;\r\n	①如持有军官证、残疾人证、老干部离退休证、导游证、学生证等参团者所有自费项目以上任何有效证件均不享受优惠活动，中途退团、项目未参加，费用均不退还；&lt;/p&gt;\r\n&lt;p&gt;\r\n	②地接质量以客人在当地意见单为凭证，如在当地没有填写意见单，我社不接受投诉。&lt;/p&gt;\r\n&lt;p&gt;\r\n	③自费景点不参加者，须自行前往酒店（费用自理）或在导游指定地点自由活动等候参加游览的客人到齐(自由活动时间等同于自费项目游览时间)一起前往酒店。&lt;/p&gt;\r\n&lt;p&gt;\r\n	④行程中部分景点依照当地管理规定，对特定持证人给予门票优惠，请您携带学生证、老年证、离退休证、军官证、残疾证等有效证件，务必提前向导游出示。如遇门票优惠，将以旅行社享受的门票折扣价格与实际优惠的门票价格的之间的差额，当地统一给您办理退还。自费项目均无优惠！&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	⑤报价中核算的机票，均为折扣价格（不保证票面），含机建费燃油税，如遇国家政策性调整，请补齐费用。一经确定报名即出票，报名确定后不得退团、改团或换名，否则将承担机票和车费损失！&lt;/p&gt;\r\n&lt;p&gt;\r\n	⑥送站说明：&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、接、送火车、机场有可能乘民航巴士或地铁等公共交通工具，特殊时间段或需专车接送，价格另议；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、导游会根据旅行团返程飞机、火车的时间做出相应处理，因旅游的淡季和旺季不同的时节导游在保障正常游览时间的情况下有可能提前或错后送机送站时间。飞机团如人数不足10人，送站时将客人送到机场大巴站，大巴费用20元/人自理。飞机返程如出现24：00以后抵达北京均视为24：00前结束行程；&lt;/p&gt;\r\n&lt;p&gt;\r\n	⑦老年人报名要求：必须身体健康、无传染病史，填写健康声明；&lt;/p&gt;\r\n','1374820266','1',NULL,'1','|上海市|浙江省|江苏省|上海市|杭州|乌镇|千岛湖|西溪湿地|苏州|南京|扬州|','老年旅游、华东、上海、杭州、苏州、南京、扬州','','','218.html','products.html','0','0','0','0','0','0','1',NULL);");
E_D("replace into `travel_products` values('190','避暑山庄+普宁寺+小布达拉宫（自理）三日','','10098','0','','5','13','19','1','2','0','天天发团','','5','5800','5380','208','提前三天报名','电联','飞机','&lt;p&gt;\r\n	◎高档酒店：全程挂四或准五标准指定酒店&lt;br /&gt;\r\n	◎精华景点：5A南山／热带天堂公园／分界洲／天涯海角／玉带滩／热带植物园／大东海／椰林乡村生态旅游区／亚龙湾旅游度假区&lt;br /&gt;\r\n	独特安排:&amp;ldquo;非诚勿扰2&amp;rdquo; 情美大片拍摄地【亚龙湾热带天堂森林公园风景区】&lt;br /&gt;\r\n	◎导游服务：专职优秀导游，每位导游均经严格筛选，经验丰富、知识面广、讲解幽默风趣灵活、服务意识强，保证全程服务无忧。&lt;br /&gt;\r\n	◎购物安排：海南最具特色的水晶店，国内首家大型免税购物商场&amp;mdash;&amp;mdash;免税店&lt;br /&gt;\r\n	◎郑重承诺：绝不推任何自费景点（特色项目除外），绝不更换景点&lt;/p&gt;\r\n','','','','1368458195','2',NULL,'1','|亚洲|韩国|济州岛|','','','','190.html','products.html','0','0','1','1','1','0','0','2');");
E_D("replace into `travel_products` values('219','<澳大利亚 新西兰 墨尔本 凯恩斯-王者大堡礁12日精华游>','经典 20人小团','XZMZYGL','0','王者大堡礁 澳新凯墨12天  银索号阿金考特大堡礁+2晚国际连锁5星酒店+澳式盛宴特色餐+澳州时令水果。无附加费。','37','41|42','','11','56','2013','2013-09-09','4006-365-001, 010-58301186','12','23800','20800','100','建议提前30天报名','全程当地四-五星级酒店或公寓','飞机往返','&lt;p&gt;\r\n	&lt;span style=&quot;font-size: 14px; &quot;&gt;悉尼 特别安排：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;悉尼歌剧院，20世纪最具特色的建筑之一，世界著名的表演艺术中心；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;悉尼水族馆，长达146米的水底通道，全部圆弧形的玻璃观景窗，尽情欣赏海底生态环境的媚姿；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;悉尼塔，澳大利亚最高的建筑，也是南半球最高的参观平台；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;悉尼杜莎夫人蜡像馆，由蜡制雕塑家杜莎夫人建于1835年，是全世界水平最高的蜡像馆之一；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;卧龙岗，蓝色海洋路最美且别具特色的一段美景，观赏神奇KIAMA喷水柱；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;特别赠送：卡通玩偶每人一个。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;凯恩斯 特别安排：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;王者大堡礁 - 阿金考特大堡礁，完美的外堡礁体验；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;特别赠送：浮潜、海底观察站、半潜水艇、凯恩斯水上平台，并在船上享用海鲜自助餐。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;布里斯班海豚岛 特别安排：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;喂食野生海豚，与海豚零距离接触；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;入住海豚岛度假村，距离海滩不过50米，穿过花园和沙滩就可来到蔚蓝的海边；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;独家赠送：每人价值10澳币代金卷，可用于岛上的餐饮及自费项目等。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;墨尔本 特别安排：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;疏芬山金矿，体验淘金乐趣；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;独家赠送：24K镀金纪念金币每人一枚。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;新西兰 特别安排：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;毛利文化村，观看地热奇观，了解当地土著居民的传统文化；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;爱歌顿农场，让您与可爱的羊驼亲密接触；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;瓦卡地热喷泉，丰富的地热形成的泥浆池日以继夜的冒着泡泡，定会让您深感震撼；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre; &quot;&gt; &lt;/span&gt;罗托鲁瓦途径Matamata小镇，它可是大名鼎鼎的《魔戒》首部曲中,霍比特人居住村庄的取景点；&lt;/span&gt;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	&lt;span style=&quot;font-size: 14px; &quot;&gt;1. 请务必保证客人在护照签名页及签证表格上亲笔签名；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2. 请务必保证客人资料的&lt;strong&gt;真实性&lt;/strong&gt;；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;注：如客人无法保证以上两点，我公司将谢绝客人参团。&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	&lt;strong style=&quot;color: rgb(255, 0, 0); font-size: 16px; &quot;&gt;旅费所包括项目:&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1. 全程机票费用、燃油附加税；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2. 全程4-5星级酒店或公寓双标住宿；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;3. 全程旅游巴士(含保险)；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;4. 全程中文导游（16人以下为司机兼导游）；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;5. 行程中所列的早餐为酒店内西式自助早餐；（如遇自助式酒店公寓，早餐为打包早餐）；（如遇早航班，早餐为酒店提供早餐盒）；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;6. 行程中所列的午晚餐（中式6菜1汤+茶水）；（如因航班时间问题，有可能安排接送机快餐）；（如因用餐时间在飞机或船上以机船餐为准，不再另退餐费）；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;7. ADS团队签证费用；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;8. 境外旅游人身伤害意外险；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;9. 行程所列景点门票。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#ff0000;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;strong&gt;旅费不包括项目:&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;1. 个人护照办理费用；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;2. 航空保险费、行李保险费、超重行李费；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;3. 司机、导游小费共计 AUD144；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;4. 酒店客房、行李员、餐馆等其它自愿支付小费；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;5. 各项私人额外费用如：洗衣、长途电话、酒水等消费；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;6. 行程安排之外任何观光项目及自费活动(包括这些活动期间的用车、导游和司机服务等费用)；&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;7. 单人间房差&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;8. 因私人原因、交通延阻、罢工、台风或其它不可抗力因素而产生的额外费用。&lt;/span&gt;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#ff0000;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;&lt;span style=&quot;background-color:#ffff00;&quot;&gt;欢迎各位贵宾参加此次澳新旅行，为了您在本次旅途中自身的安全，我们特别请您遵守下列事项，这是我们应尽告知的责任，也是保障您的权益。请仔细阅读如下内容：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;1. 集合与出发&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 出发当天请严格遵守团队集合时间，按要求的时间抵达集合地点，若无法准时抵达，务必马上联络旅行社工作人员，以免有客人迟到，航空公司关柜，客人到机场还是无法登机的严重情况发生。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 出国当天请随身携带手机，以免有迟到、塞车、转机...等意外情况而联络不到您。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;2. 航班与行李携带&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 航班经停或转机时，请务必反复确认登机口和登机时间，并请提前60分钟抵达登机口。航班登机口可能临时变更，请留意机场广播或导游提示，以免误机。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 搭乘飞机时，请您随时扣紧安全带，以免气流颠簸影响您的安全。高空上因机舱压力较大，不宜饮酒过量，以免影响身体健康。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 一般航空公司免费托运行李额度为每人1件，每件不超过20公斤，超过则需收费；手提行李以不超过2件，合计不超过10公斤，超过限制者，以托运为准。超过100毫升的液体行李(包括饮料、洗浴用品、化妆品等)、打火机、指甲钳、各式刀械等，不得手提上机，需以托运方式处理，具体规定可能根据安全形势发生变化，详情请提前咨询各航空公司。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 请将贵重物品或易碎物品及随时要取用物品放在手提行李箱内。例如：自备药品、摄象机、照相机、电池等。护照、机票、现金等贵重物品请务必随身携带，以免丢失。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 领取行李时如发生行李遗失或损坏的情况，请及时告知导游，由导游协助向航空公司办理相关手续或申请相关索赔。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;3. 海关与出入境&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 澳洲规定18岁以上成年人每人仅可带香烟50支入境，肉类及其加工品、蔬果、种子及各类违禁品，禁止携入澳大利亚及新西兰。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 入境时，请谨慎确实回答当地移民官的询问及检查，携带之现金、支票或其它外币亦应确实申报，如被发现不实或不符，可能遭到拒绝入境，甚至被罚款或拘禁。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;4. 气候与穿着&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;在澳大利亚温带地区有四季变化，季节与北半球正好相反：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;春季：09月至11月&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;夏季：12月至02月&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;秋季：03月至05月&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;冬季：06月至08月&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;澳大利亚冬季温和，全境没有太寒冷的地区。例如：悉尼的冬天温度大约在摄氏6-16度左右。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;请带好应季衣物，以免天气变化较大，容易着凉、感冒。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 由于澳大利亚部分地区日照强烈，在夏季请准备好防日晒、防雨、游泳时所必备的遮阳伞、太阳镜、防晒霜、泳装等等。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 鞋子以平底轻便的运动鞋为宜，女士请尽量不要穿高跟鞋。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;5. 酒店须知&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;酒店房间的卫生间中都提供毛巾、洗发水、香皂等。大多数酒店不提供牙膏、牙刷、拖鞋、请客人自备。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;酒店房间的迷你吧中食品以及饮料是需要客人付费的，请客人注意。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;房间内打电话都会由电脑记入帐户，等办理离店手续时，前台会通知客人交费（客人自付）。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;饭店一般有收费电视及免费电视两种，使用前请问询清洁服务员或导游收费标准、操作方法及注意事项，以免造成不必要的损失。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;澳洲电压一般是240伏特，50周交流，使用三角扁型插座。酒店一般有多制式插座，也可向前台或客房部借用。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;旅游期间，尽量避免到偏僻的地方。出行时尽量结伴而行，以便相互照顾。留意下榻酒店的名称和地址，最好向前台索取一张酒店名片，随身携带，以便外出后能够顺利回到酒店。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;6. 当地须知&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;在澳新期间城市观光使用空调旅游车，请记住车的型号、颜色、车号、以免与团队走失时可顺利找到车。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;澳洲及新西兰交通行车靠左，请注意交通安全，走人行横道。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;观光时，大件行李可放在旅游车上，贵重物品一定随身携带，以免丢失。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;国内海关出境时，我国公民允许携带的货币金额为每人5000澳币。信用卡开通境外使用服务的和银联卡都可使用&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;您可以在机场、酒店、赌场或银行兑换澳币。澳币与美金的比率为1:0.65－0.68(当天汇率),新币与美金的比率为1：0.5（此汇率仅供参考，实际请按照当时汇率牌价兑换）。此外也可以向当地导游咨询兑换。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;根据国家有关规定，持因私5年护照者，在得到使馆签证后，出境前可按照银行当时牌价在护照签发当地中国银行每人兑换5000澳币。北京、上海、广东三地持ADS团队签证的客人，出境前持护照原件，团体签证复印件，本人身份证或户口本原件及复印件到当地中国银行换汇，每人限换5000澳币。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;7. 其他&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;悉尼、墨尔本4月至11月时间比北京快2小时，11月至3月时间比北京快3小时。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;布里斯班、黄金海岸、凯恩斯全年时间比北京快2小时。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;新西兰4月至11月时间比北京快4小时，11月至3月时间比北京快5小时。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;bull;澳洲国际区号为0061，如果从国内打电话到悉尼，请打0061－2（悉尼区号）－电话号码，布里斯班、黄金海岸区号为7，墨尔本区号为3。如从澳洲往国内打电话，请拨001186－国内区号－电话号码。新西兰国际区号为0064，如果从国内打电话到奥克兰，请打0064-9（奥克兰区号）-电话号码。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;8. 旅游保险&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 本公司已为参团旅客购买价值25万人身意外保险。本公司强烈要求旅客自行购买旅游意外保险，以更全面保障旅客利益。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;9. 健康与药物&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 外出长途旅游，可能会因水土不服而令身体感到不适。故应准备些常备药物，以备不时之需。携带常备药物时，应避免是粉剂，以免被误为违禁品。孕妇及年长者或健康状况不良者，宜有家人随行。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 患有心脏病、糖尿病等慢性疾病者应随身携带药物，并听从医生指示服用，不随便吃别人的药。且应提前到医院索取附有英文说明的诊断书备用。以确保在国外发病时，可获得妥善治疗。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;10. 退税事宜&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp; &amp;nbsp; 10.1 购物退税基本步骤：&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;*. 在可以退税的某商店一次性购买超过一定金额就可以申请退税；填写退税表格并出示护照，向店员索要您的退税单据。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;*. 出境离开海关之前，在海关办理退税单据上验放盖章的手续(贵重物品需随身携带，以便海关检查)。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;*. 澳洲各主要机场都有退税服务台，退税时请出示您的护照，单据及信用卡，税金将会直接打入您的信用卡。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;11. 游客责任&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 本公司所不能控制之特殊情况下，如战争、政治动荡、天灾、恶劣天气、交通工具发生技术问题、罢工及工业行动等，本公司有权在启程前或出发后取消或替换任何一个旅游项目，亦有权缩短或延长旅程。而所引发之额外支出或损失，概与本公司无涉，团员不得借故反对或退出。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 行程中所安排之机票/船票/车票/酒店或观光项目，均属团体订位，一经出发前确认及订购后，不论任何情况下而未能使用者，概不退回任何款项。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 请严格遵守团队出发当日的集合时间、境外行程安排中及返回的各个集合时间。为保证大部分游客的利益，避免极小部分游客因迟到而影响全团游客行程安排。如有小部分游客未按照指定时间抵达集合地，经该团其他全部游客签名，导游将根据事先约定时间准时出发。因此产生的该部分游客的损失(如出租车前往下一目的地的费用或前往目的地的机票等一切相关费用)由迟到的游客自行承担。如团员故意不遵守规则或妨碍全团游客正常活动及利益时，本公司工作人员有权取消其参团资格，所缴费用恕不退还，而该团员离团后一切行动与本公司无关。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 团员须遵守各国政府之条例，严禁携带违禁品，违例者须自行负责。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 14px; &quot;&gt;12. 特别注意&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 万一遭遇强盗时，最好不要强力抵抗，以保护自身安全为要，镇静记住歹徒外形，以便警方破案。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 遭窃盗或意外事故后，请立即通知导游，并在导游协助下，前往当地警察局或驻外使领馆，并申领遗失或被窃证明文件。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 紧急时，如果语言不通，就用表情或肢体语言表达，不要一味忍耐或保持沉默，而失去先机。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 旅行途中身体若有不适，马上告知导游或同行人员，尽速送医治疗。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 途中如有诊疗、住院或购买药品，应索要诊断书及治疗证明单，以便回国后申请保险赔付。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;* 如果携带信用卡，请在国内提前向发卡银行确认激活，开通信用卡境外使用服务。境外商店也可以直接使用银联卡 。&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#0000ff;&quot;&gt;&lt;span style=&quot;font-size: 16px; &quot;&gt;&lt;strong&gt;&lt;span style=&quot;background-color:#ffff00;&quot;&gt;注：如有任何疑问欢迎来电查询。谢谢！&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n','1374820666','2',NULL,'1','|澳洲|澳大利亚|新西兰|','','','','219.html','products.html','0','0','1','0','0','0','0',NULL);");
E_D("replace into `travel_products` values('197','<加拿大东西岸全景之旅10日>','加拿大东西海岸全景深度之旅！','xzmzx','0','“赏落基山脉壮丽美景”','35','40','','11','56','8','9月16、28、30','4006-365-001；010-58301186.','10','28800','27800','73','25','3-4星级酒店','飞机','&lt;p&gt;\r\n	1、乘坐雾中少女号游船巡游尼亚加拉大瀑布；&lt;br /&gt;\r\n	2、游览班夫国家公园，搭乘冰原雪车赏哥伦比亚冰原；&lt;br /&gt;\r\n	3、入住维多利亚市酒店，直飞卡尔加里，不走回头路；&lt;br /&gt;\r\n	4、加拿大旅游局优先合作社称号，保持高出签率；&lt;/p&gt;\r\n&lt;p&gt;\r\n	5、搭乘加拿大航空直飞目的地，免除转机劳顿；&lt;/p&gt;\r\n&lt;p&gt;\r\n	6、参团客人每人可获赠加拿大特产冰酒一瓶；&lt;br /&gt;\r\n	&lt;span style=&quot;display: none&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n','&lt;p&gt;\r\n	加拿大签证需要提前办理！&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&lt;strong&gt;一、项目包含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、行程中所列酒店或同类酒店。（由于美洲无统一的酒店星级评定标准，为保证酒店品质，我社多选用国际连锁酒店集团旗下酒店或当地知名特色酒店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、行程中所含景点首道门票；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、行程中所列午，晚餐以中式餐食或自助餐为主（桌餐标准为十人一桌为六餐一汤，七人一桌为四菜一汤；自助餐标准为中式自助餐），早餐为酒店内或打包早餐（简餐或快餐）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、旅行社责任保险&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;二、项目不含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;ol&gt;\r\n	&lt;li&gt;\r\n		护照费、申请签证中准备相关材料所需的制作、手续费，如未成年人所需的公证书；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;签证费&lt;/strong&gt;。 加拿大900人民币/人；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		小费100加币/人&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;strong&gt;加拿大境内段航班行李托运费以及任何一段超出免费托运行李标准的费用；&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li&gt;\r\n		导游和司机每天正常工作时间不超过10小时，如超时加收超时费；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		转侯机/火车/船只时用餐。&lt;/li&gt;\r\n	&lt;li&gt;\r\n		因个人原因造成行境内段李托运或超重费用；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		因客观原因需要更换上述酒店所产生的房费差价；（单数报名者我社保留收取单房差的权利）&lt;/li&gt;\r\n	&lt;li&gt;\r\n		因个人疏忽、违章或违法引起的经济损失或赔偿费用；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		行程列明以外的景点或活动所引起的任何费用；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		因个人原因滞留产生的一切费用；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		一切私人费用（例如洗衣、电话、传真、上网、收费电视节目、游戏、宵夜、酒水、邮寄、机场和酒店行李搬运服务、购物、行程列明以外的用餐或宴请等费用）；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		因气候或飞机、车辆、船只等交通工具发生故障导致时间延误或行程更改引起的经济损失和责任；&lt;/li&gt;\r\n	&lt;li&gt;\r\n		行李在航班托运期间的造成损坏的经济损失和责任。&lt;/li&gt;\r\n	&lt;li&gt;\r\n		自由活动期间不提供餐、车、导游服务，如客人自由活动请告知领队导游，如因客人自身原因造成回程航班延误后果自负。&lt;/li&gt;\r\n	&lt;li&gt;\r\n		其他行程中以及上述&amp;ldquo;报价包含&amp;rdquo;条款中未列明的一切额外费用；&amp;nbsp;&lt;/li&gt;\r\n&lt;/ol&gt;\r\n','&lt;p&gt;\r\n	当您从中国离境和入境时，一定检查海关是否给您的护照盖了清晰的离境章和入境章，它是您已经回到中国的唯一凭证。由此造成不必要的损失，非常抱歉只能由本人承担！请您谅解的同时也请您自己务必仔细留意！&amp;nbsp;&lt;/p&gt;\r\n','1374741553','2',NULL,'1','|北美洲|加拿大|','','','','197.html','products.html','0','0','1','0','1','0','0',NULL);");
E_D("replace into `travel_products` values('226','<法国、意大利、瑞士VIP12日游>','法国的时尚与浪漫，瑞士的阿尔卑斯山岳风光，意大利的罗马、佛罗伦萨和威尼斯，欧洲最经典的自然风光与文化艺术完美融合一起。法国的时尚与浪漫，瑞士的阿尔卑斯山岳风光，意大利的罗马、佛罗伦萨和威尼斯，欧洲最经','XZMGLHY','0','','14','15|21|23','','11','56','2013','2013年9月21日','4006-365-001     （010）5830-1186','12','26800','25800','0','提前一个月','当地四星级酒店','飞机','&lt;p&gt;\r\n	1、国际一流航空，罗马巴黎两点进出，行程舒适轻松；&lt;br /&gt;\r\n	2、行程包含价值3000元自费：铁力士雪山；卢浮宫、凡尔塞宫含讲解；塞纳河游船。&lt;br /&gt;\r\n	3、全程七菜一汤及三个当地特色餐（法餐、意大利餐及瑞士风味餐）。&lt;br /&gt;\r\n	4、独家安排漫游贝林佐纳城堡。&lt;br /&gt;\r\n	5、特别安排法国恬静古典的后花园&amp;mdash;&amp;mdash;卢瓦尔河谷。&lt;br /&gt;\r\n	6、特别安排搭乘著名的全景观光列车&amp;mdash;金色山口快车。&lt;br /&gt;\r\n	7、30人小团；45座以上旅游车，资深中文导游服务。&lt;br /&gt;\r\n	8、适合人群：希望旅程丰富、轻松舒适的体验型游客。&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n','&lt;p&gt;\r\n	销签说明&lt;br /&gt;\r\n	1、按照欧洲各使馆现行规定，会抽查团队部分或全部出团客人回国后面试销签。使馆会在团队出团后予以通知，抽查到的客人必须按照使馆通知时间2、本人前往使馆。建议客人将返程机票订在回国当天下午或第二天；如遇回国抵达当天为周五、周六或周日，请订下周一下午或晚上的航班，以避免3、使馆通知面试而耽误航班（各使馆周五下午不对外办公）。&lt;br /&gt;\r\n	4、部分使馆通常会待客人回国几天后才通知面试消签，此种情况较难理解，但实属使馆现行做法。且所有面试费用由客人自理，请各位客人做好被通5、知面试销签准备。&lt;br /&gt;\r\n	6、欧洲使馆规定，凡在旅行社参团办理团队签证的客人须在回国抵达首都机场时将护照、全程登机牌交回使馆销签，同时，护照销签时间取决于各使7、馆的工作进度，由于旅游旺季或有大型活动等原因，使馆的工作量很大，销签的时间会长些，请客人予以理解并做好这个准备。届时会有专人向您8、收取，谢谢您的配合！&lt;br /&gt;\r\n	9、请务必保管好全程登机牌，并核对姓名拼写，发生姓名错误请及时告知导游，及时补救。若因客人自身原因丢失、缺损登机牌证明或姓名不正确，10、请抵达中国后配合旅行社第一时间前往使馆面试销签；如果不能当即销签，使馆将会通知面试核销签证，由此产生的所有费用由客人承担！！！&lt;/p&gt;\r\n','&lt;p&gt;\r\n	报价包含以下服务：&lt;br /&gt;\r\n	1、国际往返机票（含机场税），团队经济舱；&lt;br /&gt;\r\n	2、团队旅游签证费；&lt;br /&gt;\r\n	3、欧洲当地标准四星酒店双人标准间住宿（两人一间，含早）；&lt;br /&gt;\r\n	4、中式午、晚餐（七菜一汤，用餐时间在飞机或船上以机船餐为准，不再另退餐费）；5、&lt;br /&gt;\r\n	6、全程旅游巴士及专业司机；&lt;br /&gt;\r\n	7、全程中文领队兼导游服务；&lt;br /&gt;\r\n	8、旅游人身意外险；&lt;br /&gt;\r\n	9、旅行社责任险。&lt;/p&gt;\r\n&lt;p&gt;\r\n	报价不包含以下服务：&lt;br /&gt;\r\n	1、护照费；&lt;br /&gt;\r\n	2、洗衣、理发、电话、饮料、烟酒、付费电视、行李搬运等私人费用；&lt;br /&gt;\r\n	3、境外司机、导游小费（每人全程：120欧元）；&lt;br /&gt;\r\n	4、行李海关课税，超重行李托运费、管理费；&lt;br /&gt;\r\n	5、行程中未提及的景点游览及其他费用，如特殊门票、游船（轮）、缆车、地铁票等费用；&lt;br /&gt;\r\n	6、因交通延阻、罢工、大风、大雾、航班取消或更改时间等人力不可抗拒原因所引致的额外费用；&lt;br /&gt;\r\n	7、签证相关的例如未成年人公证，认证等相关费用。&lt;br /&gt;\r\n	备注：&lt;br /&gt;\r\n	1、上述行程次序、景点、航班及住宿地点可能临时变动、修改或更换，最终行程以出发前我公司确认的行程为准；&lt;br /&gt;\r\n	2、我公司保留因航空公司、签证、政府、天气等因素而更改行程和追加差价的权利。&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&lt;br /&gt;\r\n	1、当您从欧洲离境时，一定检查海关是否给您的护照盖了清晰的离境章，它是您已经回到中国的唯一凭证。如果没有盖章或者章不清晰无法辨认将会2、导致使馆要求您面试销签，由此造成不必要的损失，非常抱歉只能由本人承担！请您谅解的同时也请您自己务必仔细留意！&lt;br /&gt;\r\n	3、行程中所列航班号及时间仅供参考，将根据实际情况做出合理的调整；&lt;br /&gt;\r\n	4、欧洲同北京时间时差：夏季六小时；冬季七小时（个别国家不同地区也会有时差，均以当地到达时间为准）；&lt;br /&gt;\r\n	5、行程中所注明的城市间距离，参照境外地图，仅供参考，视当地交通状况进行调整；&lt;br /&gt;\r\n	6、依照旅游业现行作业规定，本公司有权依据最终出团人数情况，调整房间分房情况。&lt;span style=&quot;display: none&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;\r\n','1374822975','2',NULL,'1','|欧洲|法国|瑞士|意大利|','','','','226.html','products.html','0','0','1','1','1','1','1',NULL);");
E_D("replace into `travel_products` values('224','<美国东西海岸含大瀑布+黄石公园14日>','我们将带您深入探访世界上首座国家公园、地球上独一无二的神奇世界自然遗产―【黄石国家公园】； ','XZMMH','0','●精选美国最具代表的城市和景点：纽约、华盛顿、布法罗、洛杉矶、拉斯维加斯、盐湖城；\r\n●我们将带您深入探访世界上首座国家公园、地球上独一无二的神奇世界自然遗产―【黄石国家公园】； \r\n●沉醉于美国著名的旅游胜地，占地31万英亩，享有“最秀丽的国家公园”称号的【大提顿国家公园】；\r\n●漫步在【大瀑布公园】，全方位欣赏世界三大瀑布之美加边境的尼亚加拉大瀑布美丽独特的自然景观；\r\n●可自费前往【大峡谷国家公园】，亲身领略自然界七大奇景之一浩瀚气魄，举世无双的科罗拉多大峡谷；\r\n●赠送拉斯维加斯夜游，深度体验梦幻般沙漠之都最迷人的夜景；\r\n●亲身体验环球影城内3D变形金刚主题乐园—采用了最新3D技术、最顶尖视觉特效，游客不再是故事观看者，而是被设计成故事中的重要角色成为“自由战士”，和“汽车人”一起与“霸天虎”殊死搏斗\r\n●亲临美国NBA湖人队主场斯台普斯中心见证科比、霍华德、纳什的新时代组合，为喜欢篮球的朋友们选购球衣等纪念品；\r\n●前往美国西部最大的奥特莱斯——棕榈泉奥特莱斯【卡巴松大卖场Cabazon Premium Outlets】购物，130家商店打造美国“自由”购物文化\r\n●标准：全程三~四星级舒适酒店；\r\n●保险：全程SOS紧急医疗救援及意外伤害险为您畅游美洲保驾护航；\r\n●赠送：美标转换器； \r\n●行李：全程免费托运一件行李，助您尽情购物；\r\n','35','39','','11','56','2013','2013-09-13','4006-365-001   (010)5830-1186','14','29999','27500','100','提前40天','3-4星级','飞机','&lt;p&gt;\r\n	●精选美国最具代表的城市和景点：纽约、华盛顿、布法罗、洛杉矶、拉斯维加斯、盐湖城；&lt;br /&gt;\r\n	●我们将带您深入探访世界上首座国家公园、地球上独一无二的神奇世界自然遗产―【黄石国家公园】；&lt;br /&gt;\r\n	●沉醉于美国著名的旅游胜地，占地31万英亩，享有&amp;ldquo;最秀丽的国家公园&amp;rdquo;称号的【大提顿国家公园】；&lt;br /&gt;\r\n	●漫步在【大瀑布公园】，全方位欣赏世界三大瀑布之美加边境的尼亚加拉大瀑布美丽独特的自然景观；&lt;br /&gt;\r\n	●可自费前往【大峡谷国家公园】，亲身领略自然界七大奇景之一浩瀚气魄，举世无双的科罗拉多大峡谷；&lt;br /&gt;\r\n	●赠送拉斯维加斯夜游，深度体验梦幻般沙漠之都最迷人的夜景；&lt;br /&gt;\r\n	●亲身体验环球影城内3D变形金刚主题乐园&amp;mdash;采用了最新3D技术、最顶尖视觉特效，游客不再是故事观看者，而是被设计成故事中的重要角色成为&amp;ldquo;自由战士&amp;rdquo;，和&amp;ldquo;汽车人&amp;rdquo;一起与&amp;ldquo;霸天虎&amp;rdquo;殊死搏斗&lt;br /&gt;\r\n	●亲临美国NBA湖人队主场斯台普斯中心见证科比、霍华德、纳什的新时代组合，为喜欢篮球的朋友们选购球衣等纪念品；&lt;br /&gt;\r\n	●前往美国西部最大的奥特莱斯&amp;mdash;&amp;mdash;棕榈泉奥特莱斯【卡巴松大卖场Cabazon Premium Outlets】购物，130家商店打造美国&amp;ldquo;自由&amp;rdquo;购物文化&lt;br /&gt;\r\n	●标准：全程三~四星级舒适酒店；&lt;br /&gt;\r\n	●保险：全程SOS紧急医疗救援及意外伤害险为您畅游美洲保驾护航；&lt;br /&gt;\r\n	●赠送：美标转换器；&lt;br /&gt;\r\n	●行李：全程免费托运一件行李，助您尽情购物；&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&lt;br /&gt;\r\n	个人资料：&lt;br /&gt;\r\n	护照原件（有效期在9个月以上，包括已过期和作废的护照）冲洗照片两张&lt;br /&gt;\r\n	身份证、全家户口本、结婚证（如结婚证丢失，请补办或开据相关丢失证明）&lt;br /&gt;\r\n	工作资料：&lt;br /&gt;\r\n	名片两张、营业执照或组织机构代码证（原件或复印件盖章）、单位简介（彩色装订版）&lt;br /&gt;\r\n	在职证明（可以提供空白盖章单位信笺纸两张，由旅行社代做）&lt;br /&gt;\r\n	退休人员提供：退休证&lt;br /&gt;\r\n	在校学生提供：学生证,准假证明&lt;br /&gt;\r\n	财产资料：&lt;br /&gt;\r\n	自己或配偶名下的存折或存单（存折存单尽量使用存入期较长的，活期和定期均可，数额&lt;br /&gt;\r\n	不限越多越好，如使用银行储蓄卡请将流水单及余额打出来让银行盖章）&lt;br /&gt;\r\n	房产证（如房产证尚在办理中，请提供购房合同、购房发票）&lt;br /&gt;\r\n	照片资料：&lt;br /&gt;\r\n	全家福照片数张（夫妻、一家三口或四口、大家庭的都可以）&lt;/p&gt;\r\n','&lt;p&gt;\r\n	1、&amp;nbsp;酒店：全程三~四星级酒店双人标准间；（由于美国国内没有统一的酒店星级评定标准，酒店房间设施与国内同级酒店基本相同）；&lt;br /&gt;\r\n	2、用车：全程25~33座空调旅行车（包含司机和导游座位）；&lt;br /&gt;\r\n	3、用餐：早餐（酒店内美式大陆早餐，拉斯维加斯中式简早套餐送入房间）；&lt;br /&gt;\r\n	早餐是所预定酒店提供的一项不另外收取费用的服务，如果因为境外某段航班起飞时间较早，酒店不一定能够打包早餐，也希望客人能够谅解我们无法退还该早餐费用；&lt;br /&gt;\r\n	午餐、晚餐&amp;lt;中式桌餐10人/桌7菜1汤或中式自助餐或西式快餐&amp;gt;；&lt;br /&gt;\r\n	注：全程餐的安排以出团通知里的行程中关于餐的标明为准；&lt;br /&gt;\r\n	4、导服：华语导游服务；配司机和导游双人服务；&lt;br /&gt;\r\n	5、领队：全程精选双语领队服务费；&lt;br /&gt;\r\n	6、门票及特色内容：纽约：自由女神游船；费城：自由钟游览；拉斯维加斯：夜游；洛杉矶：环球影城、棕榈泉奥特莱斯；黄石国家公园；大提顿国家公园；&lt;/p&gt;\r\n','&lt;p&gt;\r\n	1、参加旅游团的客人取消用餐、酒店住宿、景点参观、旅游用车等，费用恕不退还；&lt;br /&gt;\r\n	2、酒店位置：北美城市地广人稀，很多市区酒店周围并无繁华的街道和社区；大部分城市即使在市中心，晚19-20点后商业区亦停止营业，并非像中国城市一样晚间热闹；&lt;br /&gt;\r\n	3、酒店星级：绝大部分海外国家无类似于中国的酒店星级评定制度，我方提供的酒店星级是相当于国内该星级的酒店标准；&lt;br /&gt;\r\n	4、酒店入住：国际通行酒店入住时间是下午15点之后，退房时间是中午12点之前，有的酒店在入住时还会要求客人交房间押金，退房时再退给客人；&lt;br /&gt;\r\n	5、导游可根据团队在境外旅游目的地的实际情况，在不减少参观景点的前提下调整景点参观先后顺序；&lt;br /&gt;\r\n	6、根据国际航班团队搭乘要求，团队通常须提前3-3.5小时到达机场办理登机手续，故国际段航班在当地下午15点前（含15点），晚间21点前（含21点）起飞的，行程均不含午餐或晚餐。&lt;br /&gt;\r\n	7、国际航班游客须提前3-4小时到机场，美国内陆航班游客须提前2个小时到机场，集体办理登机手续，如因自身原因未按约定时间及地点集合，错过起飞时间，产生的一切损失及相关费用由客人承担。&lt;br /&gt;\r\n	8、延时服务：国外导游和司机以每日工作10小时计算，如团员要求或团员原因引起工作时间超时，则需收加班费；&lt;br /&gt;\r\n	9、退票处理：团队机票一旦出票，费用不可退还。&lt;br /&gt;\r\n	10、美国东岸比北京时间慢13小时，4月至9月美国实行夏令时，期间则慢12小时。西岸比北京时间慢16小时，4月至9月美国实行夏令时，期间慢15小时。夏威夷比北京时间慢18小时，抵达美国后请校对手表，以免误时而影响行程。&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n','1374822497','2',NULL,'1','|北美洲|美国|','','','','224.html','products.html','0','0','0','0','1','0','0',NULL);");
E_D("replace into `travel_products` values('196','华东五市乌镇木渎双水乡超值','囊括华东著名景点：中山陵、玄武湖、夫子庙、灵山大佛、盘门三景、漫步西湖、水乡乌镇、木渎','XZMHDFC','0','囊括华东著名景点：中山陵、玄武湖、夫子庙、灵山大佛、盘门三景、漫步西湖、水乡乌镇、木渎；','6|34|33|30','76|83|84|77|78|58','','11','56','9','天天发团','010-58301186；4006365001','7','1350','1190','566','10','三星住宿','火车（可升级高铁或飞机）','&lt;p&gt;\r\n	■ 景点安排：囊括华东著名景点：中山陵、玄武湖、夫子庙、灵山大佛、盘门三景、漫步西湖、水乡乌镇、木渎；&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、住宿安排：3晚三星级酒店（非挂牌）住宿，特别赠送1晚四星级酒店（非挂牌）住宿；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、&amp;nbsp;车辆安排：专属空调旅游车，保证1人1个座位；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、用餐安排：各地随餐品尝-南京盐水鸭、无锡酱排骨、杭州西湖醋鱼；&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、&amp;nbsp;导游安排：专属优秀中文导游旅行及讲解服务；&lt;/p&gt;\r\n&lt;p&gt;\r\n	5、购物安排：全程6个诚信购特商店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	6、&amp;nbsp;门票安排：白天景点门票全含；&lt;/p&gt;\r\n&lt;p&gt;\r\n	7、郑重承诺：行程中无强制消费，自由选择行程内推荐的自费景点、娱乐项目或其它经游客允许的消费活动！&lt;/p&gt;\r\n','','&lt;p&gt;\r\n	一、报价包含：&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、住宿：3晚三星（非挂牌）双人间+1晚四星酒店（非挂牌）双人间； 如出现单男或单女请补交房差或由地接社安排三人间或双标间加床（加床为钢丝床）或拼住，不提供自然单间。共计=200元；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、用餐：4早5正，8菜1汤，早餐5元/人，正餐15元/人，10人1桌，人数不足菜量相应减少；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、交通&lt;strong&gt;：&lt;/strong&gt;去程空调火车卧铺或高铁二等、飞机；返程空调火车卧铺或高铁二等座、飞机。当地空调旅游车1人1座，全程1台车，26座以下均没有行礼箱；&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、导游：地1个导游；&lt;/p&gt;\r\n&lt;p&gt;\r\n	二、不含项目&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、保险：游客出行前自行购买旅游人身意外伤害保险及航空保险；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、车票差价：季期间火车票非常紧张，往返火车票均按行程中标注的价格核算，如出现火车软卧、动车等特殊情况，请游客按照票面补齐火车票差价，旺季不保证火车进出地点。火车团儿童只含当地车位及半价餐，飞机团12周岁以下儿童只含飞机票、当地车位及半价餐；儿童：占床4晚加200元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	三、自费项目&lt;/p&gt;\r\n&lt;p&gt;\r\n	以下项目为公司推荐的旅游项目，&lt;strong&gt;游客自愿参加需要另外付费&lt;/strong&gt;，自理景点因含有导游司机加班费故一切优惠证件不予使用。如游客持证自行游览自费景点，另加收车费100元/人/点。不参加者须自行前往酒店费用自理或在导游指定地点自由活动等侯参加游览的游客到齐后统一送回酒店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;1、&lt;strong&gt;【&lt;/strong&gt;苏州古运河】&lt;/strong&gt;或&lt;strong&gt;【山塘老街】&lt;/strong&gt;120元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;2、西湖之夜】&lt;/strong&gt;普通席240元/人、贵宾席280元/人（参加贵宾席赠送价值45元/人的西湖游船）；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;3、上海夜景】&lt;/strong&gt;280元/人（含乘船夜游黄浦江、登金茂大厦、夜景）+杜莎夫人蜡像馆150元/人=优惠价380元/人；&lt;/p&gt;\r\n&lt;p&gt;\r\n	四、购物安排&lt;/p&gt;\r\n&lt;p&gt;\r\n	全程6个店：&lt;strong&gt;【紫砂】&lt;/strong&gt;蠡湖紫砂馆或帅元紫砂博物馆（1h）&lt;strong&gt;【珍珠】&lt;/strong&gt;五湖珍珠馆或新东吴珍珠馆（1.5h）、&lt;strong&gt;【杭白菊】&lt;/strong&gt;双龙杭白菊（1.5h）、&lt;strong&gt;【丝绸】&lt;/strong&gt;杭州天蚕丝绸（2h）、&lt;strong&gt;【龙井】&lt;/strong&gt;茶香人家或梅坞问茶（1.5h）、&lt;strong&gt;【水晶】&lt;/strong&gt;上海水晶店（1.5h）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div style=&quot;clear: both&quot;&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	&lt;strong&gt;①&lt;/strong&gt;如持有军官证、残疾人证、老干部离退休证、导游证、学生证等参团者所有自费项目以上任何有效证件均不享受优惠活动&lt;strong&gt;，&lt;/strong&gt;中途退团、项目未参加，费用均不退还；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;②&lt;/strong&gt;地接质量以客人在当地意见单为凭证，如在当地没有填写意见单，我社不接受投诉。&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;③&lt;/strong&gt;自费景点不参加者，须自行前往酒店（费用自理）或在导游指定地点自由活动等候参加游览的客人到齐(自由活动时间等同于自费项目游览时间)一起前往酒店。&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;④&lt;/strong&gt;行程中部分景点依照当地管理规定，对特定持证人给予门票优惠，请您携带学生证、老年证、离退休证、军官证、残疾证等有效证件，务必提前向导游出示。如遇门票优惠，将以旅行社享受的门票折扣价格与实际优惠的门票价格的之间的差额，当地统一给您办理退还。自费项目均无优惠！&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;⑤&lt;/strong&gt;报价中核算的机票，均为折扣价格（不保证票面），含机建费燃油税，如遇国家政策性调整，请补齐费用。一经确定报名即出票，报名确定后不得退团、改团或换名，否则将承担机票和车费损失！&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;⑥送站说明：&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、接、送火车、机场有可能乘民航巴士或地铁等公共交通工具，特殊时间段或需专车接送，价格另议；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、导游会根据旅行团返程飞机、火车的时间做出相应处理，因旅游的淡季和旺季不同的时节导游在保障正常游览时间的情况下有可能提前或错后送机送站时间。飞机团如人数不足10人，送站时将客人送到机场大巴站，大巴费用20元/人自理。飞机返程如出现24：00以后抵达北京均视为24：00前结束行程；&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;⑦老年人报名要求：必须身体健康、无传染病史，填写健康声明；&lt;/strong&gt;&lt;/p&gt;\r\n','1374736299','1',NULL,'1','|上海市|浙江省|江苏省|安徽省|杭州|乌镇|千岛湖|苏州|南京|上海市|','老年旅游.华东、杭州、南京、苏州、乌镇、上海','','','196.html','products.html','0','0','1','1','0','1','1',NULL);");
E_D("replace into `travel_products` values('222','<普吉岛五星帝王>5晚7日游','全程五星级酒店','XZMZY','0','','52',NULL,'','11','','8','8月02  04  11  18  20  24  25  31','4006-365-001   010-5830-1186','7','6980','6680','50','10天','全程指定五星级酒店','飞机','&lt;p&gt;\r\n	1：全程无自费！&lt;br /&gt;\r\n	2：赠送价值人民币3980元奢华行程！&lt;br /&gt;\r\n	3: 奢华住宿：全部使用新车接待贵宾&lt;br /&gt;\r\n	4：连住芭东区国际连锁假日酒店 步行2分钟即达海边&lt;/p&gt;\r\n','','&lt;p&gt;\r\n	1.报价包含：国际机票及税金，签证费，普吉岛芭东海边国际连锁酒店&amp;mdash;【中央假日酒店】，行程所列一日三餐，行程所列景点门票，空调旅游巴士，中文导游服务，领队全程服务，保额25万人身意外保险一份。&lt;br /&gt;\r\n	2.报价不含：全程导游司机小费180元/人，境外个人消费，人力不可抗拒因素造成的一切损失。&lt;/p&gt;\r\n','','1374822019','2',NULL,'1','|东南亚|','','','','222.html','products.html','0','0','1','0','0','0','0',NULL);");
E_D("replace into `travel_products` values('223','<“美”梦成真”— 美国（加拿大）东西海岸夏威夷16日 >','总价值超300美金的传统自费项目已包含在内！','XZMZX','0','','35','39','','11','56','9','9月7、14、19、21、22、26、29','4006-365-001 / 010-58301186.','16','22800','21800','36','30天','三-四星级酒店','飞机','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	1、游览世界七大自然奇观之一[尼亚加拉大瀑布]；&lt;/div&gt;\r\n&lt;div&gt;\r\n	2、[圣地亚哥游览][华盛顿博物馆][费城]观光，&lt;/div&gt;\r\n&lt;div&gt;\r\n	3、总价值超300美金的传统自费项目已包含在内；&lt;/div&gt;\r\n&lt;div&gt;\r\n	4、夏威夷或拉斯维加斯3晚酒店，行程更加舒适；&lt;/div&gt;\r\n&lt;div&gt;\r\n	5、全程无任何强制购物店及强制自费项目；&lt;/div&gt;\r\n&lt;div&gt;\r\n	6、国航往返直飞目的地，国际段免费托运2件行李；&lt;/div&gt;\r\n&lt;div&gt;\r\n	7、独家安排全美两大连锁折扣特卖工厂直销店&lt;/div&gt;\r\n&lt;div&gt;\r\n	8、[TANGER Outlets]和[premium Outlet]畅购；&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre&quot;&gt; &lt;/span&gt;&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	美国签证需要面试！&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;一、项目包含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、行程中所列航班经济舱团体机票及相关税费；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、境外旅游巴士（每人一正座），其中15至25人（25座-29座),26至30人（34座-36座），夏威夷不计人档均为（10座-25座）；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、领队兼导游服务费；（夏威夷除外）（自由活动期间除外）；&lt;/p&gt;\r\n&lt;p&gt;\r\n	4、行程中酒店所列酒店或同类别酒店双人住宿（由于美洲无统一的酒店星级评定标准，为保证酒店品质，我社多选用当地连锁酒店集团旗下酒店，如Comfort Inn，Holiday Inn，Best western等；&lt;/p&gt;\r\n&lt;p&gt;\r\n	5、行程中所含景点首道门票：XXX&lt;/p&gt;\r\n&lt;p&gt;\r\n	6、行程中所列午，晚餐以中式餐食为主（桌餐标准为十人一桌为六餐一汤，七人一桌为五菜一汤；自助餐标准为中式自助餐），早餐为酒店内或打包早餐（简餐或快餐）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	7、旅行社责任保险&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;二、项目不含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	1.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 护照费、申请签证中准备相关材料所需的制作、手续费，如未成年人所需的公证书；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	2.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 小费 160美金/人；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	3.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 签证费。美国1200人民币/人 加拿大900人民币/人&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	&lt;strong&gt;4.&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;美国境内段航班行李托运费以及任何一段超出免费托运行李标准的费用；&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	5.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 导游和司机每天正常工作时间不超过10小时，如超时加收超时费；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	6.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 转候机/火车/船只时用餐。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	7.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 因个人原因造成行境内段李托运或超重费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	8.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 因客观原因需要更换上述酒店所产生的房费差价；&lt;strong&gt;（单数报名者我社保留收取单房差的权利）&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	9.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 因个人疏忽、违章或违法引起的经济损失或赔偿费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	10.&amp;nbsp;&amp;nbsp;&amp;nbsp; 行程列明以外的景点或活动所引起的任何费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	11.&amp;nbsp;&amp;nbsp;&amp;nbsp; 因个人原因滞留产生的一切费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	12.&amp;nbsp;&amp;nbsp;&amp;nbsp; 一切私人费用（例如洗衣、电话、传真、上网、收费电视节目、游戏、宵夜、酒水、邮寄、机场和&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	酒店行李搬运服务、购物、行程列明以外的用餐或宴请等费用）；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	13.&amp;nbsp;&amp;nbsp;&amp;nbsp; 因气候或飞机、车辆、船只等交通工具发生故障导致时间延误或行程更改引起的经济损失和责任；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	14.&amp;nbsp;&amp;nbsp;&amp;nbsp; 行李在航班托运期间的造成损坏的经济损失和责任。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	15.&amp;nbsp;&amp;nbsp;&amp;nbsp; 自由活动期间不提供餐、车、导游服务，如客人自由活动请告知领队导游，如因客人自身原因造成&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	回程航班延误后果自负。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	16.&amp;nbsp;&amp;nbsp;&amp;nbsp; 其他行程中以及上述&amp;ldquo;报价包含&amp;rdquo;条款中未列明的一切额外费用；&lt;/p&gt;\r\n','','1374822140','2',NULL,'1','|北美洲|美国|','','','','223.html','products.html','0','0','1','0','0','0','0',NULL);");
E_D("replace into `travel_products` values('220','<“美”梦成真— 美国东西海岸14日 >','为客人出行便捷，拉斯维加斯入住STRIP大道酒店； 拉斯维加斯独家安排3晚酒店，市场唯一在拉斯维加斯安排游览行程的团队，包含[胡佛水坝]，[米德尔湖]，[premium Outlet]等更多精彩； ','XZMZX','0','','35','39','','11','56','9','9月1、4、10、17、21、25、30','4006-365-001 / 010-58301186.','14','18800','18300','32','25','三-四星级酒店','飞机','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	1、游览世界七大自然奇观之一的[尼亚加拉大瀑布]；&lt;/div&gt;\r\n&lt;div&gt;\r\n	2、已包含[圣地亚哥]游览观光，[费城]游览，等总价值超200美金的传统自费项目；&lt;/div&gt;\r\n&lt;div&gt;\r\n	3、独家游览有世界上最甜美地方之称的[哈里斯堡]；&lt;/div&gt;\r\n&lt;div&gt;\r\n	4、独家安排全美两大连锁折扣特卖工厂直销店&lt;/div&gt;\r\n&lt;div&gt;\r\n	5、[TANGER Outlets]和[premium Outlet]畅购；&lt;/div&gt;\r\n&lt;div&gt;\r\n	6、为客人出行便捷，拉斯维加斯入住STRIP大道酒店；&lt;/div&gt;\r\n&lt;div&gt;\r\n	7、拉斯维加斯独家安排3晚酒店，市场唯一在拉斯维加斯安排游览行程的团队，包含[胡佛水坝]，[米德尔湖]，[premium Outlet]等更多精彩；&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	美国签证需要面试！&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	一、项目包含&lt;/div&gt;\r\n&lt;div&gt;\r\n	1、行程中所列航班经济舱团体机票及相关税费；&lt;/div&gt;\r\n&lt;div&gt;\r\n	2、境外旅游巴士（每人一正座），其中15至25人（25座-29座),26至30人（34座-36座）；&lt;/div&gt;\r\n&lt;div&gt;\r\n	3、领队兼导游服务（自由活动期间除外）；&lt;/div&gt;\r\n&lt;div&gt;\r\n	4、行程中酒店标准双人间（由于美洲无统一的酒店星级评定标准，为保证酒店品质，我社多选用当地连锁酒店集团旗下酒店，如Comfort Inn，Days Inn，Best western等；&lt;/div&gt;\r\n&lt;div&gt;\r\n	5、行程中所含景点首道门票；&lt;/div&gt;\r\n&lt;div&gt;\r\n	6、行程中所列午，晚餐以中式餐食为主（桌餐标准为十人一桌六菜一汤，七人一桌为五菜一汤；自助餐标准为&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp; &amp;nbsp;中式自助餐），早餐为酒店内美式早餐或打包早餐（如遇较早起飞航班，拉斯维加斯早餐为酒店外早餐或打包快餐）；如遇用餐时间在国际航班上以机上餐为准，美国内陆航班不提供餐食；&lt;/div&gt;\r\n&lt;div&gt;\r\n	7、旅行社责任保险；&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n&lt;div&gt;\r\n	二、项目不含&lt;/div&gt;\r\n&lt;div&gt;\r\n	1.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;护照费、申请签证中准备相关材料所需的制作、手续费，如未成年人所需的公证书；&lt;/div&gt;\r\n&lt;div&gt;\r\n	2.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;小费140美金/人；&lt;/div&gt;\r\n&lt;div&gt;\r\n	3.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;美国签证费1200人民币/人；&lt;/div&gt;\r\n&lt;div&gt;\r\n	4.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;美国境内段航班行李托运费以及任何一段超出免费托运行李标准的费用；&lt;/div&gt;\r\n&lt;div&gt;\r\n	5.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;转候机时用餐；&lt;/div&gt;\r\n&lt;div&gt;\r\n	6.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;内陆段行李托运（内陆段将根据当地规定及各航空公司规定收取行李费用，需自付），或因个人原因造成超重所产生的费用；&lt;/div&gt;\r\n&lt;div&gt;\r\n	7.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;因单人入住所产生的房费差价；（单数报名者我社保留收取单房差的权利）&lt;/div&gt;\r\n&lt;div&gt;\r\n	8.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;因个人疏忽、违章或违法引起的经济损失或赔偿费用；&lt;/div&gt;\r\n&lt;div&gt;\r\n	9.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;一切私人费用（例如洗衣、电话、传真、上网、收费电视节目、游戏、宵夜、酒水、邮寄、机场和酒店行李搬运服务、购物、行程列明以外的用餐或宴请等费用）；&lt;/div&gt;\r\n&lt;div&gt;\r\n	10.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;因气候或飞机、车辆、船只等交通工具发生故障导致时间延误或行程更改引起的经济损失和责任；&lt;/div&gt;\r\n&lt;div&gt;\r\n	11.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;行李在航班托运期间的造成损坏的经济损失和责任；&lt;/div&gt;\r\n&lt;div&gt;\r\n	12.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;自由活动期间不提供餐、车、导游服务，如脱团请务必提前告知领队；&lt;/div&gt;\r\n&lt;div&gt;\r\n	13.&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt; &lt;/span&gt;客人自身原因造成回程航班延误或因个人原因滞留产生的一切费用；&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','','1374820738','2',NULL,'1','|北美洲|美国|','','','','220.html','products.html','0','0','1','0','0','0','0',NULL);");
E_D("replace into `travel_products` values('198','<蓝白物语系列之一：【碧云天】>','沙美岛一晚住宿 白天自费600封顶','XZMNAFQ','0','沙美岛一晚住宿','52','53','','11','','0','每月多团','4006-365-001  5830-1186','6','4380','4280','2589','提前20天','四星+五星','飞机','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	1.&amp;nbsp;【生态游】沙美岛国家海洋自然保护风景区。&lt;/p&gt;\r\n&lt;p&gt;\r\n	2.&amp;nbsp;【健康游】泰式古法按摩（90分钟）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	3.&amp;nbsp;【浪漫游】沙美岛海滩PUB星光之夜、昭帕雅公主号豪华游船－夜游湄南河。&lt;/p&gt;\r\n&lt;p&gt;\r\n	4.&amp;nbsp;【休闲游】四合镇水乡、东芭乐园、俱乐部吉祥休闲3合1。&lt;/p&gt;\r\n&lt;p&gt;\r\n	5.&amp;nbsp;【贵宾游】金东尼人妖歌舞表演（VIP席）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	6.&amp;nbsp;【美食游】绿光森林生态餐，皇家御厨火锅，东芭乐园自助餐，游船国际自助餐。&lt;/p&gt;\r\n&lt;p&gt;\r\n	7.&amp;nbsp;【品质保证】&lt;/p&gt;\r\n&lt;p&gt;\r\n	1)&amp;nbsp;保证自费600元封顶.&lt;/p&gt;\r\n&lt;p&gt;\r\n	2)&amp;nbsp;自费完全遵照泰旅局自费规定价格，保证决不强迫自费，由客人自愿选择。&lt;/p&gt;\r\n&lt;p&gt;\r\n	3)&amp;nbsp;保证使用专业合法的有证导游。&lt;/p&gt;\r\n','&lt;p&gt;\r\n	半年以上有效期护照 &amp;nbsp;白底两寸照片2张&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left:-26.6700pt;margin-left:50.0000pt;&quot;&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	报价包含&lt;/div&gt;\r\n&lt;div&gt;\r\n	国际机票及境外机场税、北京出境机场税、燃油附加费、当地四星级酒店双人标准间住宿、行程中所列餐食(自由活动期间餐费自理、如遇飞机上用餐则不另外安排)、空调旅游大巴、行程内指定景点门票、领队服务费、签证费。&lt;/div&gt;\r\n&lt;div&gt;\r\n	报价不含&lt;/div&gt;\r\n&lt;div&gt;\r\n	护照费、个人旅游意外保险、境外司机导游服务小费（泰国小费RMB30元/人天）以及上述未列出之个人费用。&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n','','1374742997','2',NULL,'1','|东南亚|泰国|','蜜月旅游','','','198.html','products.html','0','0','1','0','0','0','0',NULL);");
E_D("replace into `travel_products` values('201','<加拿大落基山 温哥华 惠斯勒10日>','赏落基山脉美景！','XZMZX','0','“赏落基山脉壮丽美景”','35','40','','11','56','9','9月22日；10月3日','4006-365-001；010-58301186.','10','25800','24800','76','30','三-四星级酒店','飞机','&lt;p&gt;\r\n	1、乘坐雾中少女号游船巡游尼亚加拉大瀑布；&amp;nbsp;&lt;/p&gt;\r\n&lt;div&gt;\r\n	2、游览班夫国家公园，搭乘冰原雪车赏哥伦比亚冰原；&lt;/div&gt;\r\n&lt;div&gt;\r\n	3、入住维多利亚市酒店，直飞卡尔加里，不走回头路；&lt;/div&gt;\r\n&lt;div&gt;\r\n	4、加拿大旅游局优先合作社称号，保持高出签率；&lt;/div&gt;\r\n&lt;div&gt;\r\n	5、搭乘加拿大航空直飞目的地，免除转机劳顿；&lt;/div&gt;\r\n&lt;div&gt;\r\n	6、参团客人每人可获赠加拿大特产冰酒一瓶；&lt;/div&gt;\r\n&lt;div&gt;\r\n	&amp;nbsp;&lt;/div&gt;\r\n','&lt;p&gt;\r\n	美国签证费1200/人&lt;/p&gt;\r\n','&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;一、项目包含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n	1、行程中所列航班经济舱团体机票及相关税费；&lt;/p&gt;\r\n&lt;p&gt;\r\n	2、境外旅游巴士（每人一正坐），其中15至25人（25座-29座),26至30人（34座-36座）；&lt;/p&gt;\r\n&lt;p&gt;\r\n	3、领队基本服务费（自由活动期间除外），境外中文陪同（自由活动期间除外）；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 15.75pt&quot;&gt;\r\n	4、行程中所列酒店或同类酒店。（由于美洲无统一的酒店星级评定标准，为保证酒店品质，我社多选用国际连锁酒店集团旗下酒店或当地知名特色酒店；&lt;/p&gt;\r\n&lt;p&gt;\r\n	5、行程中所含景点首道门票；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 15.75pt&quot;&gt;\r\n	6、行程中所列午，晚餐以中式餐食或自助餐为主（桌餐标准为十人一桌为七餐一汤，七人一桌为四菜一汤；自助餐标准为中式自助餐），早餐为酒店内或打包早餐（简餐或快餐）。&lt;/p&gt;\r\n&lt;p&gt;\r\n	7、旅行社责任保险&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;二、项目不含&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	1.&amp;nbsp;&amp;nbsp; 护照费、申请签证中准备相关材料所需的制作、手续费，如未成年人所需的公证书；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	2.&amp;nbsp;&amp;nbsp; &lt;strong&gt;签证费&lt;/strong&gt;。 加拿大900人民币/人；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	3.&amp;nbsp;&amp;nbsp; 小费100加币/人&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	&lt;strong&gt;4.&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;加拿大境内段航班行李托运费以及任何一段超出免费托运行李标准的费用；&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	5.&amp;nbsp;&amp;nbsp; 导游和司机每天正常工作时间不超过10小时，如超时加收超时费；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	6.&amp;nbsp;&amp;nbsp; 转侯机/火车/船只时用餐。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	7.&amp;nbsp;&amp;nbsp; 因个人原因造成行境内段李托运或超重费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	8.&amp;nbsp;&amp;nbsp; 因客观原因需要更换上述酒店所产生的房费差价；（单数报名者我社保留收取单房差的权利）&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	9.&amp;nbsp;&amp;nbsp; 因个人疏忽、违章或违法引起的经济损失或赔偿费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	10.&amp;nbsp; 行程列明以外的景点或活动所引起的任何费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	11.&amp;nbsp; 因个人原因滞留产生的一切费用；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	12.&amp;nbsp; 一切私人费用（例如洗衣、电话、传真、上网、收费电视节目、游戏、宵夜、酒水、邮寄、机场和酒店行李搬运服务、购物、行程列明以外的用餐或宴请等费用）；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	13.&amp;nbsp; 因气候或飞机、车辆、船只等交通工具发生故障导致时间延误或行程更改引起的经济损失和责任；&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	14.&amp;nbsp; 行李在航班托运期间的造成损坏的经济损失和责任。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	15.&amp;nbsp; 自由活动期间不提供餐、车、导游服务，如客人自由活动请告知领队导游，如因客人自身原因造成回程航班延误后果自负。&lt;/p&gt;\r\n&lt;p style=&quot;margin-left: 21pt&quot;&gt;\r\n	16.&amp;nbsp; 其他行程中以及上述&amp;ldquo;报价包含&amp;rdquo;条款中未列明的一切额外费用；&lt;/p&gt;\r\n','&lt;p style=&quot;margin-left: 18pt&quot;&gt;\r\n	当您从中国离境和入境时，一定检查海关是否给您的护照盖了清晰的离境章和入境章，它是您已经回到中国的唯一凭证。由此造成不必要的损失，非常抱歉只能由本人承担！ 请您谅解的同时也请您自己务必仔细留意！&lt;/p&gt;\r\n','1374805190','2',NULL,'1','|北美洲|加拿大|','','','','201.html','products.html','0','0','1','0','0','0','0',NULL);");
